//             {expiresIn: '12h'})
//         }
//         res.json(response);
//     })
// }
//funcion para crear un empleado nuevo
exports.create = function (req, res) {
  var hashedpass = crypto.createHash("sha512").update(req.body.pass).digest("hex");
  var empleado = new Empleado({
    nombre: req.body.nombre,
    apellido_p: req.body.apellido_p,
    apellido_m: req.body.apellido_m,
    telefono: req.body.telefono,
    mail: req.body.mail,
    direccion: req.body.direccion
    //pass: req.body.pass
    //pass: hashedpass
  });

  //guarda el empleado nuevo
  empleado.save().then(function () {
    response.exito = true, response.msg = "El empleado se guardó correctamente";
    console.log('Empleado guardado exitosamente');
    res.json(response);
  })["catch"](function (error) {
    console.error('Error al guardar el empleado', error);
  });
};

//funcion para mostrar todos los datos de los empleados
exports.find = function (req, res) {
  Empleado.find().then(function (empleados) {
    return res.json(empleados);
  });
};

//funcion para mostrar el dato de un empleado dado su id.
exports.findById = function (req, res) {
  Empleado.findById(req.params.id).then(function (empleados) {
    return res.json(empleados);
  });
};

//funcion para actualizar los datos de un empleado dado su id.
exports.update = function (req, res) {
  var empleado = {
    nombre: req.body.nombre,
    apellido_p: req.body.apellido_p,
    apellido_m: req.body.apellido_m,
    telefono: req.body.telefono,
    mail: req.body.mail,
    direccion: req.body.direccion,
    pass: req.body.pass
  };
  //actualiza un contacto usando el id_
  Empleado.findByIdAndUpdate(req.params.id, {
    $set: empleado
  }).then(response.exito = true, response.msg = "El empleado se actualizó correctamente", res.json(response))["catch"](function (error) {
    response.exito = false, response.msg = "Error al actualizar el empleado", res.json(response);
  });
};
exports.remove = function (req, res) {
